+bool AllowOutboundOnion()
+{
+    if (!gArgs.IsArgSet("-onlynet")) return true;
+    for (const auto& net : gArgs.GetArgs("-onlynet")) {
+        if (ParseNetwork(net) == NET_ONION) return true;
+    }
+    return false;
+}
