+    uint64_t nConflictingCount = 0;
+    for (const auto& mi : setIterConflicting) {
+        for (const CTxIn &txin : mi->GetTx().vin)
+        {
+            setConflictsParents.insert(txin.prevout.hash);
+        }
+
+        nConflictingCount += mi->GetCountWithDescendants();
