cr ACK d4f3fe1bb3e8d0e1d048bd1922adbc671081b249

If you retouch, could move the string as `static std::string` into `FormatFullVersion`?

```diff
diff --git a/src/clientversion.cpp b/src/clientversion.cpp
index 29c38e2d3b..8ea5aa86f8 100644
--- a/src/clientversion.cpp
+++ b/src/clientversion.cpp
@@ -40,8 +40,6 @@ const std::string CLIENT_NAME("Satoshi");
     #endif
 #endif
 
-const std::string CLIENT_BUILD(BUILD_DESC BUILD_SUFFIX);
-
 static std::string FormatVersion(int nVersion)
 {
     return strprintf("%d.%d.%d", nVersion / 10000, (nVersion / 100) % 100, nVersion % 100);
@@ -49,6 +47,7 @@ static std::string FormatVersion(int nVersion)
 
 std::string FormatFullVersion()
 {
+    static const std::string CLIENT_BUILD{BUILD_DESC BUILD_SUFFIX};
     return CLIENT_BUILD;
 }
