+    msg->m_raw_message_size = CHACHA20_POLY1305_AEAD_AAD_LEN + m_message_size + CHACHA20_POLY1305_AEAD_TAG_LEN; // raw wire size
+
+    // store receive time
+    msg->m_time = time;
+
+    Reset();
+    return msg;
+}
