+
+        bool is_sp_desc = (desc_str.rfind("sp(", 0) == 0);
+
+        if (is_sp_desc) {
+            range = {0, wallet::SILENT_ADDRESS_MAXIMUM_IDENTIFIER};
+        }
+
+        UniValue range_uni = find_value(scanobject, "range");
+        if (!range_uni.isNull()) {
+            range = ParseDescriptorRange(range_uni);
+        }
+
+        if (is_sp_desc && range.second > wallet::SILENT_ADDRESS_MAXIMUM_IDENTIFIER) {
+            std::stringstream ss;
+            ss << wallet::SILENT_ADDRESS_MAXIMUM_IDENTIFIER;
+
+            throw JSONRPCError(RPC_INVALID_PARAMETER,
+                "End of range is too high. The maximum allowed for silent payment descriptors is " + ss.str());
+        }
+
+    } else {
+        throw JSONRPCError(RPC_INVALID_PARAMETER, "Scan object needs to be either a string or an object");
+    }
+
+    return {desc_str, range};
