+    auto pipes = m_sockets_pipes.PopFront();
+    std::vector<CAddress> addresses{5};
+
+    DebugLogHelper debug_log{strprintf("Received addr: %u addresses", addresses.size())};
+    pipes->recv.PushNetMsg(NetMsgType::ADDRV2, addresses);
+    debug_log.EndAndThrowIfNotFound(30s);
+}
