+QIcon NetworkStyle::getAppIcon(const QSize size) const
+{
+    // load pixmap
+    QPixmap pixmap(":/icons/bitcoin");
+
+    if(pixmap.size().width() != size.width() && pixmap.size().height() != size.height())
+    {
+        QPixmap scaledPixmap = pixmap.scaled(size, Qt::KeepAspectRatio);
+        if(!scaledPixmap.isNull())
+        {
+            pixmap = scaledPixmap;
+        }
+    }
+
+    if(iconColorHueShift != 0 && iconColorSaturationReduction != 0)
+    {
+        // copy the pixmap because on linux the original pixmap will be affected
+        pixmap = pixmap.copy();
+
+        // generate QImage from QPixmap
+        QImage img = pixmap.toImage();
+
+        int h,s,l,a;
+
+        // traverse though lines
+        for(int y=0;y<img.height();y++)
+        {
+            QRgb *scL = reinterpret_cast< QRgb *>( img.scanLine( y ) );
+
+            // loop through pixels
+            for(int x=0;x<img.width();x++)
+            {
+                // preserve alpha because QColor::getHsl doesen't return the alpha value
+                a = qAlpha(scL[x]);
+                QColor col(scL[x]);
+
+                // get hue value
+                col.getHsl(&h,&s,&l);
+
+                // rotate color on RGB color circle
+                // 70ÃÂ° should end up with the typical "testnet" green
+                h+=iconColorHueShift;
+
+                // change saturation value
+                if(s>iconColorSaturationReduction)
+                {
+                    s -= iconColorSaturationReduction;
+                }
+                col.setHsl(h,s,l,a);
+
+                // set the pixel
+                scL[x] = col.rgba();
+            }
+        }
+
+        //convert back to QPixmap
+        pixmap.convertFromImage(img);

